const path = require('path');
const fs = require('fs');

function clearSavedLogs() {
  const allLogsPath = path.resolve('./logs/all-logs.log');
  const errorLogsPath = path.resolve('./logs/error.log');
  if (fs.existsSync(allLogsPath)) {
    fs.unlinkSync(allLogsPath);
  }
  if (fs.existsSync(errorLogsPath)) {
    fs.unlinkSync(errorLogsPath);
  }
}

module.exports = clearSavedLogs;
